using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using HelloWorldMVC2.Models;

namespace HelloWorldMVC2.Controllers
{
    public class ProductController : Controller
    {
        static List<Product> dsProduct = new List<Product>()
        {
            new Product(){Id=1,Name ="Ipad",Price=220,PictureURL="data:image/jpeg;base64,"},
            new Product(){Id=2,Name ="Iphone",Price=330,PictureURL="data:image/jpeg;base64,"},
            new Product(){Id=3,Name ="Samsung",Price=444,PictureURL="data:image/jpeg;base64,"},
        };

        public IActionResult Delete(int Id)
        {
            var p = dsProduct.Where(p => p.Id == Id).SingleOrDefault();
            dsProduct.Remove(p);
            return RedirectToAction("Index");
        }
        
        public IActionResult DoCreate(Product inputProduct)
        {
            var isValid = true;
            //kiem tra id
            if (dsProduct.Exists(p=>p.Id==inputProduct.Id))
            {
                ModelState.AddModelError("Id", "Id da ton tai");
                isValid = false;
            }
            //kiem tra ten
            if (inputProduct.Name ==null || inputProduct.Name.Trim().Length <=4)
            {
                ModelState.AddModelError("Name", "Do dai ten phai >=5 ky tu");
                isValid = false;
            }

            if (isValid==false)
            {
                return View("Create");
            }
            else
            {
                dsProduct.Add(inputProduct);
                return RedirectToAction("Index");
            }            
        }
        public IActionResult Create()
        {
            return View();
        }
        public IActionResult Index()
        {
            return View(dsProduct);
        }
    }
}
